insert into Artist(artistID, fullname, image, description) values ('IWZA0CI6', N'Châu Đăng Khoa', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/a/5/a589320b9286d5ba29f132b6f71e29e6_1503572225.jpg', N'Châu Đăng Khoa từng theo học Nhạc viện Thành phố Hồ Chí Minh, khoa Thanh nhạc. Anh cũng bắt đầu sáng tác để có thể thể hiện ca khúc của mình trên sân khấu.
