insert into Artist(artistID, fullname, image, description) values ('IW6ZAIBA',  N'Quyn Si', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/6/2/5/b/625bc8253eb1c57356fd1bcd9a4c7ed7.jpg', N'Với niềm đam mê nghệ thuật từ nhỏ, Quyn Si theo học chuyên ngành múa trường Cao đẳng múa Việt Nam. Cô nhận được nhiều lời mời tham gia sự kiện trong và ngoài nước. Bên cạnh đó, Quyn Si còn là gương mặt đại diện cho một số nhãn hàng mỹ phẩm và thời trang. Được sự ủng hộ của người thân và bạn bè, Quyn Si theo học thanh nhạc và thử sức ở vai trò ca sĩ. Sản phẩm âm nhạc đầu tay là Single đầu tay Vờ như một sáng tác của DC Tâm.')
