insert into Artist(artistID, fullname, image, description) values ('IWZ99A86', N'Trúc Nhân', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/a/5/3/1/a531a372e227cbb00fba0f1822c67919.jpg', N'Trúc Nhân được biết đến khi tham gia chương trình Giọng hát Việt mùa đầu tiên khi đang theo học Mỹ Thuật. Vì đam mê âm nhạc, anh đã chuyển hẳn sang ca hát.
