insert into Artist(artistID, fullname, image, description) values ('IWZAWBCZ',  N'Võ Kiều Vân', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/1/2/1290b7ed82ecda7161439ff65c02fa50_1511493608.jpg', N'Võ Kiều Vân sinh ra trong gia đình không có ai theo nghệ thuật, với năng khiếu bộc phát từ nhỏ. 5 tuổi Võ Kiều Vân đã tham gia đội ca múa và các hoạt động của đài truyền hình như “Vườn âm nhạc” “Búp Sen Hồng” HTV, VTV.
