insert into Artist(artistID, fullname, image, description) values ('IWZAOAZZ',  N'B Ray', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/0/1/9/b/019b877c85fb021c8c8d569f4737bbbe.jpg', N'Sinh ra ở Việt Nam nhưng định cư ở Mỹ, B Ray bắt đầu nghe rap từ năm 2007 và tập tành chơi nhạc từ đó, đến năm 2013 thì anh được chú ý trong giới rapper underground, đặc biệt là thông qua các bài rap "diss" (chê bai) LK, Rhymastic, Sơn Tùng M-TP...
