insert into Artist(artistID, fullname, image, description) values ('IWZ97CDD',  N'Trịnh Thiên Ân', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/2/b/0/a/2b0a58db5aa451d5a111fcaa29fd05e9.jpg', N'Trịnh Thiên Ân
