insert into Artist(artistID, fullname, image, description) values ('IWZ9ZD8A', N'Trịnh Thăng Bình', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/6/1/0/a/610ac5860a6cce05fc08a057db7fd05d.jpg', N'Trịnh Thăng Bình là một ca sĩ, nhạc sĩ, diễn viên và nhà sản xuất âm nhạc nổi tiếng Việt Nam.
