insert into Artist(artistID, fullname, image, description) values ('IW6WBZWO', N'AMEE', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/9/8/4/d/984de8b3596498462d5f954eadbb1f47.jpg', N'Có vẻ ngoài dễ thương và chất giọng ngọt ngào, AMEE gặt hái thành công với những ca khúc nhạc Pop có phần lời thả thính trong sáng như Exs Hate Me, Anh Nhà Ở Đâu Thế, Đen Đá Không Đường...
