insert into Artist(artistID, fullname, image, description) values ('IWZ97Z8D', N'Saka Trương Tuyền', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/9/e/7/e/9e7e7fc7f9eb380d7c72fb3f025171e8.jpg', N'Saka Trương Tuyền là cháu gái của Kim Tiểu Long. Cô từng đính hôn cùng nam ca sĩ Khưu Huy Vũ nhưng dừng lại sau một năm vì không hợp.
