insert into Artist(artistID, fullname, image, description) values ('IWZAZ8U6', N'Hoàng Dũng', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/a/f/3/9/af39001b5e767b0a620aede5e1b863f0.jpg', N'Ca sĩ Nguyễn Hoàng Dũng sinh ngày 5-3-1995 tại Tỉnh Thái Nguyên
