insert into Artist(artistID, fullname, image, description) values ('IWZ9ZB76',  N'Khắc Việt', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/8/4/84ffbc3c35a04d698f157a129e68d51a_1499686528.jpg', N'Tốt nghiệp khoa Thanh nhạc nhưng Khắc Việt bắt đầu với việc sáng tác.
