insert into Artist(artistID, fullname, image, description) values ('IWZ968UO',  N'Hồ Quang Hiếu', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/a/2/3/0/a2309e99e71954d95969bc53224318dc.jpg', N'Hồ Quang Hiếu tốt nghiệp trường Văn hóa Nghệ thuật Quân đội Hà Nội và vào Thành phố Hồ Chí Minh để lập nghiệp năm 2010.
