insert into Artist(artistID, fullname, image, description) values ('IW6ZAAB8',  N'ACV', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/5/e/0/6/5e06d48fb79175a096e059ecf3d8b1e8.jpg', N'ACV là công ty sản xuất và truyền thông nhạc góp phần tạo nên tên tuổi của Thương Võ, Như Việt. Ngoài ra ACV còn hợp tác với nhiều nghệ sĩ khác như Minh Vương M4U, Châu Khải Phong...')
insert into Artist(artistID, fullname, image, description) values ('IW68U68B',  N'BMZ', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/cover/3/2/9/d/329db6472f2f802bb9432c671d43b042.jpg', N'BMZ Entertainment sở hữu mạng lưới truyền thông bền vững đa chiều - đa phương tiện từ KOLs, PR Booking đến Comics, Production. Với đội ngũ năng động và chuyên nghiệp, chúng tôi luôn cập nhật, đón đầu xu hướng thị trường song song với việc phát triển đa dạng nhiều giải pháp sáng tạo, đổi mới, thích hợp với từng ngành hàng. Cùng BMZ Entertainment chinh phục thế giới kỹ thuật số - kết nối không giới hạn!')
insert into Artist(artistID, fullname, image, description) values ('IWZFD77I',  N'Masew', N'https://photo-resize-zmp3.zmdcdn.me/w360_r1x1_jpeg/avatars/2/d/6/1/2d61242d058a36761db4b60c563a410d.jpg', N'Masew gắn bó với âm nhạc từ năm lớp 10 và dần nổi tiếng trong giới underground bằng những bản remix bắt tai. Anh là thành viên của AVN Team chuyên Rap, Hiphop, EDM.
<br>Năm 2017, Masew gây chú ý cho giới nghe nhạc đại chúng khi cùng Nhật Nguyễn, một tên tuổi khác của giới underground phối lại ca khúc "Túy Âm" của Xesi. Ca khúc nhanh chóng đạt thứ hạng cao trên các bảng xếp hạng âm nhạc.
<br>Từ đó, những bản phối của Masew rất được quan tâm, có thể kể đến "Điều Khác Lạ" (Đạt G, Ngọc Haleyy), "Buồn Của Anh" (K-ICM, Đạt G), "Kém Duyên" (Rum, NIT)... đặc biệt ca khúc "Em Hơi Mệt Với Bạn Thân Anh" do Trang Pháp sáng tác, Hương Giang trình bày với bản phối của Masew giành được giải thưởng lớn của Zing Music Awards.')
